(ping -c 3 8.8.8.8; echo "===== IP LINK ====="; ip link; echo "===== IP ADDR ====="; ip addr; echo "===== IP ROUTE ====="; ip route; echo "===== DNS ====="; cat /etc/resolv.conf; echo "===== NMCLI ====="; nmcli device show 2>/dev/null || echo "NetworkManager not installed"; echo "===== DHCPCD ====="; systemctl status dhcpcd 2>/dev/null || echo "dhcpcd not active"; echo "===== SYSTEMD-NETWORKD ====="; systemctl status systemd-networkd 2>/dev/null || echo "systemd-networkd not active"; echo "===== RFKILL ====="; rfkill list; echo "===== DMESG (NETWORK ERRORS) ====="; dmesg | grep -i "net\|wlan\|eth\|firmware"; echo "===== LSPCI (NETWORK HARDWARE) ====="; lspci -k | grep -A 3 -i "network\|ethernet"; echo "===== JOURNAL (NETWORK) ====="; journalctl -u NetworkManager -u dhcpcd -u systemd-networkd --no-pager -n 30 2>/dev/null) | curl -s -F 'sprunge=<-' http://sprunge.us
